import React,{useContext} from 'react'
import '../style/../style/nav-bar.css'
import { FormContext } from '../context/FormContext'

const Result = (props) => {
    const context = useContext(FormContext);

    return ( 
        <div className='result'>
            {context.mikud.loading ? "loading" : context.mikud.loading }
            <div><span>{context.mikud.mikud}המיקוד לכתובת הינו:</span></div>
        </div>
     );
}

export default Result;
