import React from 'react'
import { useFetch } from './useFetch'
export const FormContext = React.createContext()



const FormProvider = (props) => {
    // const [mikud, setmikud] = React.useState({ mikud: null, loading: false })



    const {data, loading} = useFetch(data,`http://localhost:5001/${data.city}/${data.street}/${data.homeNumber}/${data.entrance}`)

    // eslint-disable-next-line react-hooks/exhaustive-deps






    return (
        <FormContext.Provider value={{data,loading }} >
            {props.children}
        </FormContext.Provider>
    )

}

export default FormProvider
