    // eslint-disable-next-line react-hooks/exhaustive-deps
    const onSubmit =(data) =>{
        setState(data)
        console.log(data)
    }


    useEffect(() => {
        console.log(state)
        console.log(apiUrl)
    }, [apiUrl, onSubmit, state]);

    const fatchData = (apiUrl) => {
        axios.get(apiUrl)
    }

     return (
         <FormContext.Provider value={{state,onSubmit,mikud}} >
             {props.children}
         </FormContext.Provider>
     )

 }

 export default FormProvider
