import React from 'react'
import axios from 'axios'
export const FormContext = React.createContext()



const FormProvider = (props) => {
    const [state, setState] = React.useState({city: '',street: '',homeNumber: 0 ,entrance: 0 })
    const [mikud, setmikud] = React.useState({ mikud: null, loading: null })



    // eslint-disable-next-line react-hooks/exhaustive-deps


    const onSubmit = (data) => {
        const apiUrl = `http://localhost:5001/${data.city}/${data.street}/${data.homeNumber}/${data.entrance}`
            console.log(state)
        setmikud({ mikud: null, loading: true })
        console.log(mikud)

        axios.get(apiUrl)
            .then(mikud =>  console.log(mikud))
            .catch(err => console.log(err))
    }



    return (
        <FormContext.Provider value={{ onSubmit, mikud, }} >
            {props.children}
        </FormContext.Provider>
    )

}

export default FormProvider
