import React, { useContext } from 'react'
import { useForm } from 'react-hook-form'
import Result from './Result'
import { FormContext } from '../context/FormContext'
import PlacesAutocomplete, { geocodeByAddress, getLatLng, } from 'react-places-autocomplete';

const Form = (props) => {
    const context = useContext(FormContext);

    const [address, setAddress] = React.useState({})


    const handleChange = address => {
        this.setAddress({ address });
    };

    const handleSelect = address => {
        geocodeByAddress(address)
            .then(results => getLatLng(results[0]))
            .then(latLng => console.log('Success', latLng))
            .catch(error => console.error('Error', error));
    };
    const { register, handleSubmit, errors } = useForm()

    return (
        <div>
            <form className="form-style-7" onSubmit={handleSubmit(context.onSubmit)}>
                <ul>
                    <li>
                        <label htmlFor="city">עיר</label>
                        <PlacesAutocomplete type="text" name="city" maxLength="100" value={address} onChange={handleChange} onSelect={handleSelect} ref={register({ required: true })}
   >
   {({ getInputProps, suggestions, getSuggestionItemProps, loading }) => (
     <div>
       <input
         {...getInputProps({
           placeholder: 'Search Places ...',
           className: 'location-search-input',
         })}
       />
       <div className="autocomplete-dropdown-container">
         {loading && <div>Loading...</div>}
         {suggestions.map(suggestion => {
           const className = suggestion.active
             ? 'suggestion-item--active'
             : 'suggestion-item';
           // inline style for demonstration purpose
           const style = suggestion.active
             ? { backgroundColor: '#fafafa', cursor: 'pointer' }
             : { backgroundColor: '#ffffff', cursor: 'pointer' };
           return (
             <div
               {...getSuggestionItemProps(suggestion, {
                 className,
                 style,
               })}
             >
               <span>{suggestion.description}</span>
             </div>
           );
         })}
       </div>
     </div>
   )}
 </PlacesAutocomplete>
);
}
}
