import React from 'react'
import axios from 'axios'
export const FormContext = React.createContext()



const FormProvider = (props) => {
    const [state, setState] = React.useState({data:{ } })
    const [mikud, setmikud] = React.useState({ mikud: null, loading: null })

    const apiUrl = `http://localhost:5001/${state.data.city}/${state.data.street}/${state.data.homeNumber}/:${state.data.entrance}`


    // eslint-disable-next-line react-hooks/exhaustive-deps


    const onSubmit = (data) => {
        setState({data})
        setmikud({ mikud: null, loading: true })
        axios.get(apiUrl)
            .then(mikud => setmikud({ mikud: JSON.stringify(mikud), loading: false }))

            .catch(err => console.log(err))
    }



    return (
        <FormContext.Provider value={{ state, onSubmit, mikud, }} >
            {props.children}
        </FormContext.Provider>
    )

}

export default FormProvider
